// import { Component } from '@angular/core';
import { Component, OnInit } from '@angular/core';
import { Book } from '../book'
import { BookService } from '../book.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-create-book',
  templateUrl: './create-book.component.html',
  styleUrls: ['./create-book.component.css']
})
export class CreateBookComponent implements OnInit {
  book: Book = new Book();
  constructor(private bookService: BookService,
    private router: Router) { }
  ngOnInit(): void { }
  onSubmit() {
    this.saveBook();
  }

  saveBook() {
    this.bookService.createBook(this.book).subscribe(data => {
      console.log(data);
      this.goToBookList();
    },
      error => console.log(error));
  }

  goToBookList() {
    this.router.navigate(['/books']);
  }

}
<h2 style="text-align: center;">Create Book</h2>
<div class="d-flex justify-content-center">
    <div class="bg-light p-4 border rounded">
        <!-- <div class="col-md-6 offset-md-3"> -->
            <br>    
            <form (ngSubmit)="onSubmit()">

                <div class="form-group">
                    <label>Book Title</label>
                    <input type="text" class="form-control" id="BookTitle" [(ngModel)]="book.bookTitle" name="Book Title">
                </div>

                <div class="form-group">
                    <label>Author Name</label>
                    <input type="text" class="form-control" id="authorName" [(ngModel)]="book.authorName" name="Author Name">
                </div>

                <div class="form-group">
                    <label>Publication Year</label>
                    <input type="text" class="form-control" id="publicationYear" [(ngModel)]="book.publicationYear" name="Publication Year">
                </div>

                <br>

                <button class="btn btn-success" type="submit">Submit</button>

            </form>
        <!-- </div> -->
    </div>
</div>
