import { Injectable } from '@angular/core';
import { BookFilter } from './book-filter';

import { Book } from './book';
import { Observable } from 'rxjs';
import { HttpClient, HttpParams } from '@angular/common/http';



@Injectable({
  providedIn: 'root'
})
export class BookService {
  BookList: Book[] = [];


  private baseURL="http://localhost:8080/api/v1/books";

  constructor(private httpClient: HttpClient) { }



  getBooksList(): Observable<Book[]>{
    return this.httpClient.get<Book[]>(`${this.baseURL}`);
  }

  createBook(book: Book): Observable<Object>{
    return this.httpClient.post(`${this.baseURL}`, book);
  }

  getBookById(id: number): Observable<Book>{
    return this.httpClient.get<Book>(`${this.baseURL}/${id}`);
  }

  getBookByMethods(author?: string, title?: string, id?: number): Observable<Book> {
    // Construct query parameters based on provided values
    let params = new HttpParams();
    if (author) {
      params = params.set('author', author);
    }
    if (title) {
      params = params.set('title', title);
    }
    if (id) {
      params = params.set('id', id.toString());
    }

    // Make the HTTP request with the constructed query parameters
    return this.httpClient.get<Book>(`${this.baseURL}/search`, { params });
  }



  updateBook(id: number, book: Book): Observable<Object>{
    return this.httpClient.put(`${this.baseURL}/${id}`, book);
  }

  deleteBook(id: number): Observable<Object>{
    return this.httpClient.delete(`${this.baseURL}/${id}`);
  }

  searchBookById(id: number): Observable<Book> {
    return this.httpClient.get<Book>(`${this.baseURL}/${id}`);
  }
}
