import { Component, OnInit } from '@angular/core';
import { Book } from '../book'
import { BookService } from '../book.service'
import { Router } from '@angular/router';
import { BookFilter } from '../book-filter';
@Component({
  selector: 'app-book-list',
  templateUrl: './book-list.component.html',
  styleUrls: ['./book-list.component.css']
})
export class BookListComponent implements OnInit {

  filter = new BookFilter();

  books: Book[];

  constructor(private bookService: BookService,
    private router: Router) { }

  ngOnInit() {
    this.getBooks();
    // this.search();
  }

  private getBooks(){
    this.bookService.getBooksList().subscribe(data => {
      this.books = data;
    });
  }
   bookDetails(id: number){
     this.router.navigate(['book-details', id]);
   }

   searchById() {
    if (this.enteredId) {
      this.bookService.getBookById(this.enteredId).subscribe(
        result => {
          this.book = result;
          console.log('Book found:', result);
        },
        error => {
          console.error('Error fetching book:', error);
          this.book = null; // Reset book on error
        }
      );
    }


  //  search() {
  //   console.log('fi',this.filter);
  //   this.bookService.load(this.filter);
  //   console.log(this.bookService.load);
  //   console.log(this.bookService.load(this.filter));
  // }

  updateBook(id: number){
    this.router.navigate(['update-book', id]);
    console.log('hello');
  }

  deleteBook(id: number)
  {
    this.bookService.deleteBook(id).subscribe( data =>{
      console.log(data);
      this.getBooks();
    })
  }



}
